mapper.update:
UPDATE ai_client SET
    client_name = #{clientName},
    description = #{description},
    status = #{status},
    update_time = #{updateTime}
WHERE client_id = #{clientId}

mapper.select:
SELECT id, client_id, client_name, description, status, create_time, update_time
FROM ai_client
WHERE client_id = #{clientId}

SELECT id, client_id, client_name, description, status, create_time, update_time FROM ai_client WHERE status = 1 AND client_id IN
#{clientId}
ORDER BY create_time DESC
