mapper.update:
UPDATE ai_client_rag_order SET
    rag_name = #{ragName},
    knowledge_tag = #{knowledgeTag},
    status = #{status},
    update_time = #{updateTime}
WHERE rag_id = #{ragId}

mapper.select:
SELECT id, rag_id, rag_name, knowledge_tag, status, create_time, update_time
FROM ai_client_rag_order
WHERE rag_id = #{ragId}
