mapper.update:
UPDATE ai_client_system_prompt
prompt_name = #{promptName}, prompt_content = #{promptContent}, description = #{description}, status = #{status}, update_time = now()
WHERE prompt_id = #{promptId}

mapper.select:
SELECT id, prompt_id, prompt_name, prompt_content, description, status, create_time, update_time
FROM ai_client_system_prompt
WHERE prompt_id = #{promptId}

SELECT id, prompt_id, prompt_name, prompt_content, description, status, create_time, update_time FROM ai_client_system_prompt WHERE status = 1 AND prompt_id IN
#{systemPromptId}
ORDER BY create_time DESC
